mapper.select:
select
id, student_id, exam_id, class_id, student_name, certify, mail_address, foreign_lanaguage,
student_sex, nation, political, card_id, telephone, subject_type, tocollege, tocampus,
tomajor, birthplace, grade, isstate, birthday, note, add_time, add_userid, edit_time, edit_userid
from s_student_info

    and id = #{id}
    and student_id LIKE CONCAT('%',#{studentId},'%')
    and exam_id LIKE CONCAT('%',#{examId},'%')
    and class_id = #{classId}
    and student_name LIKE CONCAT('%',#{studentName},'%')

    and certify = #{certify}




        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from s_student_info

    and id = #{id}
    and student_id LIKE CONCAT('%',#{studentId},'%')
    and exam_id LIKE CONCAT('%',#{examId},'%')
    and class_id = #{classId}
    and student_name LIKE CONCAT('%',#{studentName},'%')

    and certify = #{certify}

mapper.update:
update s_student_info
student_id = #{studentId}, exam_id = #{examId}, class_id = #{classId}, student_name = #{studentName}, certify = #{certify}, mail_address = #{mailAddress}, foreign_lanaguage = #{foreignLanaguage}, student_sex = #{studentSex}, nation = #{nation}, political = #{political}, card_id = #{cardId}, telephone = #{telephone}, subject_type = #{subjectType}, tocollege = #{tocollege}, tocampus = #{tocampus}, tomajor = #{tomajor}, birthplace = #{birthplace}, grade = #{grade}, isstate = #{isstate}, birthday = #{birthday}, note = #{note}, add_time = #{addTime}, add_userid = #{addUserid}, edit_time = #{editTime}, edit_userid = #{editUserid},
where id = #{id}
